import React, { useState, useEffect } from "react";
import axios from "axios";

function CandidateList() {
  const [candidates, setCandidates] = useState([]);
  const [filteredCandidates, setFilteredCandidates] = useState([]);
  const [languages, setLanguages] = useState([]);
  const [languageFilter, setLanguageFilter] = useState("");
  const [experienceFilter, setExperienceFilter] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:7000/candidates")
      .then((response) => {
        setCandidates(response.data.candidates);
        setLanguages(response.data.languages);
      })
      .catch((error) => {
        console.error(error);
      });
  }, []);

  useEffect(() => {
    // Filter candidates by language and experience level
    let filtered = candidates.filter((candidate) => {
      let languageMatches = false;
      let experienceMatches = false;

      if (languageFilter) {
        languageMatches = candidate.languages.includes(languageFilter);
      } else {
        languageMatches = true;
      }

      if (experienceFilter === "junior") {
        experienceMatches =
          !candidate.beginYear ||
          new Date().getFullYear() - candidate.beginYear < 3;
      } else if (experienceFilter === "senior") {
        experienceMatches =
          candidate.beginYear &&
          new Date().getFullYear() - candidate.beginYear >= 3;
      } else {
        experienceMatches = true;
      }

      return languageMatches && experienceMatches;
    });

    // Sort candidates by last update date in descending order
    filtered.sort(
      (a, b) => new Date(b.lastUpdateDate) - new Date(a.lastUpdateDate)
    );

    setFilteredCandidates(filtered);
  }, [candidates, languageFilter, experienceFilter]);

  return (
    <div>
      <h1>Candidates</h1>
      <div>
        <label>
          Language:
          <select
            value={languageFilter}
                      onChange={(e) => { console.log(e.ta) }}
          >
            <option value="">All</option>
            {languages.map((language) => (
              <option key={language.id} value={language.id}>
                {language.name}
              </option>
            ))}
          </select>
        </label>
        <label>
          Experience:
          <select
            value={experienceFilter}
            onChange={(e) => setExperienceFilter(e.target.value)}
          >
            <option value="">All</option>
            <option value="junior">Junior</option>
            <option value="senior">Senior</option>
          </select>
        </label>
      </div>
      <ul>
        {filteredCandidates.map((candidate) => (
          <li key={candidate.id}>{candidate.name}</li>
        ))}
      </ul>
    </div>
  );
}

export default CandidateList;
